  const [ingredients, setUserIngredients] = useState([]);

  const addIngredientHandler = ingredient => {
    setUserIngredients();
  };

  return (
    <div className="App">
      <IngredientForm />

      <section>
        <Search />
        <IngredientList ingredients={ingredients} />
      </section>
    </div>
  );
};

export default Ingredients;
